document.getElementsByTagName("title")[0].innerHTML = "Details";
var pid = getId();
//根据文件名（pid）来区分feedback不同的email
var Category = "";
var email = "";
var _email = "feedback_android@ushareit.com";
if (getMoudle() == "ht") {
  Category = "help_trans";
  email = _email;
} else if (getMoudle() == "hc") {
  Category = "help_connect";
  email = _email;
} else if (getMoudle() == "hcoin") {
  Category = "help_coins";
  email = "coin_voc@ushareit.com";
} else if (getMoudle() == "hd") {
  Category = "help_device";
  email = _email;
} else if (getMoudle() == "hp") {
  Category = "help_premium";
  email = "premium_voc@ushareit.com";
} else if (getMoudle() == "hs") {
  Category = "help_storage";
  email = _email;
} else {
  Category = "";
  email = _email;
};
feedbackParams = {
  "inner_func_type": "41",
  "email": email,
  "category_id": Category,
  "portal": "help_feedback_common"
};

function goFeadback() {
  try {
    client;
    var i = client.getDeviceInfo();
    var oi = JSON.parse(i);
    var title = document.getElementById("head").innerHTML;
    if ((oi.app_id == "com.lenovo.anyshare.gps" && parseInt(oi.app_ver) >= 4030500) || (oi.app_id == "com.ushareit.daily")) {
      client.handleAction(title, 8, JSON.stringify(feedbackParams))

    }
  } catch (e) {
    $("feadback_btn").removeAttribute("onclick");
    $("feadback_btn").setAttribute("href", "https://www.facebook.com/bestSHAREit");
    console.log(e)
  }
}

function collect(e, bl) {
  if (e == "btn_1") {
    $("btn_1").className = "btn " + "click";
    $("btn_2").className = "btn " + "unclick";
    $("btn_2").removeAttribute("onclick")
  } else {
    $("btn_2").className = "btn " + "click";
    $("btn_1").className = "btn " + "unclick";
    $("btn_1").removeAttribute("onclick")
  }
  try {
    client;
    if (bl == true) {
      client.analyticsEvent("UF_WHUseful", pid)
    } else if (bl == false) {
      client.analyticsEvent("UF_WHNoUseful", pid)
    }
  } catch (e) {
    console.log(e)
  }
}

function $(id) {
  return document.getElementById(id)
}
function getId() {
  var hr = window.location.href;
  var arr = hr.split("/");
  var filename = arr[arr.length - 1];
  var idarr = filename.split(".");
  var pid = idarr[0];
  return pid;
}

function getMoudle() {
  var hr = window.location.href;
  var arr = hr.split("/");
  var filename = arr[arr.length - 1];
  var idarr = filename.split("_");
  var pid = idarr[0];
  return pid;
}